    /**
     * Sortable.
     *
     * @private
     */
    function _setupSortable() {
      $(".uploaded_tracks__tracks").sortable({
        axis:'y',
        forcePlaceholderSize: true,
        cursor: 'move',
        update: _trackListSortUpdate,
        connectWith: '.uploaded_tracks__tracks'
      });
    }

    function _trackListSortUpdate(event, ui) {
      // strategy
      // track_list から舐めて、dom要素のindex を設定したあと、sort をかける
      // TODO: ここで、a_side, b_side, c_side, d_side を crossすることを前提に処理する
      var types = ['a_side', 'b_side', 'c_side', 'd_side', 'bonus'];
      for (var t = 0; t < types.length; t++) {
        for (var i = 0; i < track_list[types[t]].length; i++) {
          var track = track_list[types[t]][i];
          var track_vue = track.component;
          if (track_vue) {
            track_vue.index = $(track_vue.$el).index();
            track_vue.track_number = track_vue.index + 1;
          } else {
            console.log(types[t], i, track, 'is removed');
          }
        }

        track_list[types[t]].sort(function(a, b) {
          return (a.component ? a.component.index : Number.MAX_VALUE) - (b.component ? b.component.index : Number.MAX_VALUE);
        });
        $(".uploaded_tracks__tracks").width();
      }
    }

