    /**
     * TrackのSidePanelの情報を更新.
     * @param side_type
     * @private
     */
    function _updateTrackInfo(side_type) {
      var values = {};
      var total_time = 0;
      var tracks = 0;
      var type = _getVinylSizeTypeString(side_type);
      values.total_tracks = track_list[type].length;

      for (var i = 0; i < track_list[type].length; i++) {
        if (!track_list[type][i]._destroy) {
          if (track_list[type][i].duration >= 0) {
            total_time += track_list[type][i].duration;
            tracks++;
          }
        }
      }
      values.total_tracks = tracks;
      values.total_time = total_time;
      values.max_time = max_time_table[root_vue.vinyl_size][root_vue.rpm];
      if (type != 'bonus') {
        values.ratio = Math.min(100, Math.floor(values.total_time / values.max_time * 100));
        values.overflow = (values.total_time > values.max_time);
      }

      if (type == 'a_side') {
        root_vue.a_side_duration = values.total_time;
        leftPanel.updateAsideInfo(values);
      } else if (type == 'b_side') {
        root_vue.b_side_duration = values.total_time;
        leftPanel.updateBsideInfo(values);
      } else if (type == 'c_side') {
        root_vue.c_side_duration = values.total_time;
        leftPanel.updateCsideInfo(values);
      } else if (type == 'd_side') {
        root_vue.d_side_duration = values.total_time;
        leftPanel.updateDsideInfo(values);
      } else {
        console.log("updateTrackInfo for bonus", values);
        leftPanel.updateBonusTracksInfo(values);
      }
      _updateCompleteStatus();
    }

    /**
     * Track ListのViewを生成.
     * @param data
     * @returns {Vue}
     * @private
     */
    function _createTrackView(data) {
      return root_vue.$addChild({
        inherit: true,
        el: function () {
          var element = document.createElement('div');
          element.setAttribute("class", "edit_mode");
          return element;
        },
        template: '#track-template',
        data: data,
        computed: {
          waveform_style: function() {
            if (!this.waveform_url) {
              return ''
            }
            return this.waveform_url ? 'url(' + this.waveform_url + ')' : '';
          }
        },
        methods: {
          edit: function () {
            // TBC: もう使っていない？
            $(this.$el).toggleClass('edit_mode')
          },
          remove: function () {
            _removeTrackView(this);
          },
          toggle_sell_in_digital: function(){
            if (!this.audio_file_id) {
              return;
            }
            this.sell_in_digital = !this.sell_in_digital;
            if (this.sell_in_digital) {
              this.free_download = false;
            }
            this.update_download_condition();
          },
          toggle_order_bonus: function() {
            if (!this.audio_file_id || this.side == 'bonus') {
              return;
            }
            this.order_bonus = !this.order_bonus;
            if (this.order_bonus) {
              this.free_download = false;
            }
            this.update_download_condition();
          },
          toggle_free_download: function() {
            if (!this.audio_file_id || this.side == 'bonus') {
              return;
            }
            this.free_download = !this.free_download;
            if (this.free_download) {
              this.sell_in_digital = false;
              this.order_bonus = false;
            }
            this.update_download_condition();
          },
          remove_sample_track: function() {
            this.sample_url = '';
            this.waveform_url = '';
            this.sample_audio_file_id = '';
            this.sound_cloud_track_id = '';
            this.sound_cloud_track_duration = '';
            this.sample_progress = -1;
            $(this.$el).removeClass('status_loaded status_generated');
          },
          remove_digital_track: function() {
            this.audio_file_id = '';
            this.sell_in_digital = false;
            this.order_bonus = false;
            this.free_download = false;
            this.original_filename = '';
            this.channels = 0;
            this.sample_rate = 0;
            this.progress = -1;
            this.download_condition = 'vinyl_only';
          },
          open_track_dialog: function() {
            // TrackView 追加後の Upload Sample File ボタン押下時の処理
            var that = this;
            addTrackDialog.open(this.side, function(eventType, type, value) {

              if (eventType == 'accept') {
                that.sample_progress = 0;
                if (type == 'audio') {
                  that.progress = 0;
                }
              } else if (eventType == 'uploadprogress') {
                that.sample_progress = value;
                if (type == 'audio') {
                  that.progress = value;
                }
              } else if (eventType == 'error') {
                window.alert(value);
                that.remove_sample_track();
                if (type == 'audio') {
                  that.remove_digital_track();
                }
              } else if (eventType == 'success') {
                that.apply_track_dialog_callback(type, value);
              }
            });
          },
          apply_track_dialog_callback: function(type, info) {
            if (type === 'empty') {
              return;
            }

            if (type === 'soundcloud') {
              if (info.kind == 'track') {
                this.waveform_url = info.waveform_url;
                this.sound_cloud_track_id = info.id;
                this.sound_cloud_track_duration = info.duration;
                this.duration = Math.floor(info.duration / 1000);
                this.minutes = Math.floor(info.duration / 1000 / 60);
                this.seconds = d3.format("02")(Math.floor(info.duration / 1000) % 60);
                if (this.title == '') {
                  this.title = info.title;
                  this.artist_name = info.user.username;
                }
              } else if (info.kind == 'playlist') {
                console.log(info);
                console.log(info.tracks);
                if (info.tracks) {
                  var component = this;
                  for (var i = 0; i < info.tracks.length; i++) {
                    const track = info.tracks[i];
                    console.log(track);
                    component.waveform_url = track.waveform_url;
                    component.sound_cloud_track_id = track.id;
                    component.sound_cloud_track_duration = track.duration;
                    component.duration = Math.floor(track.duration / 1000);
                    component.minutes = Math.floor(track.duration / 1000 / 60);
                    component.seconds = Math.floor(track.duration / 1000) % 60;
                    if (component.title == '') {
                      component.title = track.title;
                      component.artist_name = track.user.username;
                    }
                    if (i < info.tracks.length - 1) {
                      const vt = new project.models.VinylTrackModel(this.side, track_list[this.side].length + 1);
                      component = _addNewTrack(vt);
                      $(component.$el).addClass('status_loaded status_generated');
                      $(component.$el).find('.uploaded_track').attr('data-audio-state', 'pause');
                    }
                  }
                }
              }
            }
            if (type === 'sample' || type === 'audio') {
              this.waveform_url = info.waveform_url;
              this.sample_audio_file_id = info.audio_id;
              this.sample_url = info.track_url;
              this.duration = info.file_meta.duration;
              this.minutes = Math.floor(info.file_meta.duration / 60);
              this.seconds = d3.format("02")(Math.floor(info.file_meta.duration) % 60);
              if (type === 'audio') {
                this.audio_file_id = info.audio_id;
                this.original_filename = info.file_meta.original_filename;
                this.file_size = info.file_meta.file_size;
                this.format = info.file_meta.format;
                this.sample_rate = info.file_meta.sample_rate;
                this.channels = info.file_meta.channels;
                this.frames = info.file_meta.frames;
                this.sections = info.file_meta.sections;
              }
              if (this.title == '') {
                this.title = info.file_meta.original_filename.split(/\.(?=[^.]+$)/)[0];  // TODO
              }
            }
            $(this.$el).addClass('status_loaded status_generated');
            $(this.$el).find('.uploaded_track').attr('data-audio-state', 'pause');
          },
          update_download_condition: function () {
            if (this.free_download) {
              this.download_condition = 'free_download';
            } else if (!this.sell_in_digital && !this.order_bonus) {
              this.download_condition = 'vinyl_only';
            } else if (this.sell_in_digital && !this.order_bonus) {
              this.download_condition = 'available';
            } else if (!this.sell_in_digital && this.order_bonus) {
              this.download_condition = 'preorder_bonus';
            } else {
              this.download_condition = 'sell_and_preorder_bonus';
            }
          },
          update_duration: function() {
            var m = parseInt(this.minutes);
            if (isNaN(m)) {
              m = 0;
            }
            var s = parseInt(this.seconds);
            if (isNaN(s)) {
              s = 0;
            }
            this.duration = (m * 60) + s;
            _updateTrackInfo(this.side_type);
          }
        },
        ready: function() {
          var that = this;
          // Upload File (for Digital Download) ボタン用のDropzone前処理
          var $uploadDom = $(this.$el).find('.btn__track_upload.digital > .btn__click_area__icon');
          if ($uploadDom.length) {
            var token = $("meta[name='csrf-token']").attr('content');
            var dropzone = new Dropzone($uploadDom[0], {
              url: '/upload/track',
              addRemoveLinks: false,
              maxFilesize: 500,
              maxFiles: 1,
              headers: {'X-CSRF-Token': token},
              previewsContainer: false,
              sending: function (file, xhr, fd) {
                fd.append("track_type", 'audio');
              },
              accept: function(_file, done) {
                if ((_file.type != 'audio/wav' &&
                     _file.type != 'audio/flac' &&
                     _file.type != 'audio/aiff' &&
                     _file.type != 'audio/x-wav' &&
                     _file.type != 'audio/x-flac' &&
                     _file.type != 'audio/x-aiff' &&
                     _file.type != 'application/octet-stream' &&
                     _file.type != 'binary/octet-stream')) {
                  done('Invalid File Type: ' + _file.type);
                  return false;
                }
                _file.target = that;
                _file.target.progress = Math.floor(0);
                done();
              },
              uploadprogress: function(_file, _progress, _size){
                if (_file.target) {
                  _file.target.progress = Math.floor(_progress);
                }
              },
            });
            dropzone.on('success', function (_file, _return, _xml) {
              if (_return.result === true) {
                console.log('_file_meta', _return.file_meta);
                that.audio_file_id = _return.file_id;
                that.original_filename = _return.file_meta.original_filename;
                that.file_size = _return.file_meta.file_size;
                that.format = _return.file_meta.format;
                that.sample_rate = _return.file_meta.sample_rate;
                that.channels = _return.file_meta.channels;
                that.frames = _return.file_meta.frames;
                that.sections = _return.file_meta.sections;

                that.duration = _return.file_meta.duration;
                that.minutes = Math.floor(_return.file_meta.duration / 60);
                that.seconds = d3.format("02")(Math.floor(_return.file_meta.duration) % 60);

                if (that.title == '') {
                  that.title = _return.file_meta.original_filename.split(/\.(?=[^.]+$)/)[0];  // TODO
                }

              } else {
                window.alert(_return.message)
                that.remove_digital_track();
              }
            });
            dropzone.on('error', function (_file, _error_msg) {
              window.alert(_error_msg)
            });
            dropzone.on('addedfile', function (file) {
              if (this.files.length > 1) {
                for (var i = 0; i < this.files.length - 1; i++) {
                  this.removeFile(this.files[i]);
                }
              }
            });
          }
          // 一度saveした後の編集の場合（durationを保持していないためここでtack情報を取得する）
          if (this.sound_cloud_track_id && !this.sound_cloud_track_duration) {
            qrates.modules.SCWrapper.getTrackInfoById(this.sound_cloud_track_id).then(function(track){
              that.sound_cloud_track_duration = track.duration;
              that.waveform_url = track.waveform_url;
            });
          }
          // ここで original_audio_file_meta から 各フィールドに展開する

          that.$watch('minutes', function(newVal, oldVal){
            this.update_duration();
          });
          that.$watch('seconds', function(newVal, oldVal){
            this.update_duration();
          });
        }
      });
    }

