    /**
     * VinylSideTypeの文字列を取得.
     * @param side_type
     * @returns {string}
     * @private
     */
     function _getVinylSizeTypeString(side_type) {
       var type = '';
       if (side_type === VINYL_SIDE_TYPE.A_SIDE) {
         type = 'a_side';
       } else if (side_type === VINYL_SIDE_TYPE.B_SIDE) {
         type = 'b_side';
       } else if (side_type === VINYL_SIDE_TYPE.C_SIDE) {
         type = 'c_side';
       } else if (side_type === VINYL_SIDE_TYPE.D_SIDE) {
         type = 'd_side';
       } else {
         type = 'bonus';
       }
       return type;
     }

     function _getVinylSideIdFromString(side_type) {
       if (side_type === 'a_side') {
         return VINYL_SIDE_TYPE.A_SIDE;
       } else if (side_type === 'b_side') {
         return VINYL_SIDE_TYPE.B_SIDE;
       } else if (side_type === 'c_side') {
         return VINYL_SIDE_TYPE.C_SIDE;
       } else if (side_type === 'd_side') {
         return VINYL_SIDE_TYPE.D_SIDE;
       } else if (side_type === 'bonus') {
         return VINYL_SIDE_TYPE.BONUS;
       } else {
         return undefined;
       }
     }

    /**
     * TrackViewを差し込むRootDomを取得.
     * @param side_type
     * @returns {*}
     * @private
     */
    function _getAddTracksRootDom(side_type) {
      if (side_type === VINYL_SIDE_TYPE.A_SIDE) {
        return document.querySelector('.uploaded_tracks--aside > .uploaded_tracks__tracks');
      } else if (side_type === VINYL_SIDE_TYPE.B_SIDE) {
        return document.querySelector('.uploaded_tracks--bside > .uploaded_tracks__tracks');
      } else if (side_type === VINYL_SIDE_TYPE.C_SIDE) {
        return document.querySelector('.uploaded_tracks--cside > .uploaded_tracks__tracks');
      } else if (side_type === VINYL_SIDE_TYPE.D_SIDE) {
        return document.querySelector('.uploaded_tracks--dside > .uploaded_tracks__tracks');
      } else if (side_type === VINYL_SIDE_TYPE.BONUS) {
        return document.querySelector('.uploaded_tracks--bonus > .uploaded_tracks__tracks');
      }
      return null;
    }

  /**
   * Trackの画面を追加する.
   *
   * @private
   * @param vinyl_track
   */
    function _addNewTrack(vinyl_track) {
      var track = vinyl_track.track;
      var side = vinyl_track.side || 'a_side';
      var side_type = _getVinylSideIdFromString(side);
      var trackModel = new project.models.TrackModel(track_list[side].length, side_type, side, vinyl_track);
      var component = _createTrackView(trackModel);
      var target_dom = _getAddTracksRootDom(side_type);
      if (track.sample_audio_file_id || track.sound_cloud_track_id) {
        $(component.$el).addClass('status_loaded status_generated');
        $(component.$el).find('.uploaded_track').attr('data-audio-state', 'pause');
      }
      component.$appendTo(target_dom);
      trackModel.component = component;
      track_list[side].push(trackModel);
      root_vue.track_models.push(trackModel);
      _updateTrackInfo(trackModel.side_type);
      _trackListSortUpdate();
      _updateCompleteStatus();
      return component;
    }

