    /**
     * コスト計算.
     * @private
     */
    function _getPressingsEstimate(cb) {
      var $cost_dom = $('.item_status__costs--total, .item_status__breakdown--num');
      $cost_dom.removeClass('show').addClass('hide');
      project.Util.getPressingsEstimate(root_vue, function (err, result) {
        if (err) {
          console.log(err);
          return;
        }
        root_vue.estimate.unit_cost = result.total_amount / result.condition.volume;
        // total costの調整.
        if (root_vue.base_funding_quantity < root_vue.self_purchase_quantity) {
          result.total_amount = Math.floor(root_vue.estimate.unit_cost * root_vue.self_purchase_quantity);
        }
        costDetailDialog.setupCost(result);
        statusPanel.setupCost(result);
        root_vue.estimate.total_cost = result.total_amount;
        $cost_dom.addClass('show').removeClass('hide');
        root_vue.self_purchase_price = utilModule.int2money(utilModule.money2int(root_vue.estimate.unit_cost, root_vue.currency), root_vue.currency);
        // TODO: serverside, revenue simulator に統合すべき
        console.log('condition', result.condition);

        // とりあえず
        _getRevenueSimulation();
        if (cb) {
          cb();
        }
      });
    }

    function _updatePaidyAvailable() {
      var to_b = function(str, force) {
        if (str === true) return true;
        if (str === 'true') return true;
        if (str === 'false') return false;
        if (str === null) return force ? false : null;
        if (str === undefined) return force ? false : undefined;
        return false;
      };
      console.log(to_b(root_vue.indefinite, true), moment(root_vue.start_date), moment(root_vue.finish_date), moment(root_vue.start_date).add(60, 'days').isBefore(moment(root_vue.finish_date)))
      if ((root_vue.currency == 'JPY') && (root_vue.ship_country == 'JP')) {
        if (root_vue.product_type === 'funding') {
          if (to_b(root_vue.indefinite, true) || moment(root_vue.start_date).add(59, 'days').isBefore(moment(root_vue.finish_date))) {
            root_vue.paidy_available = false;
          } else {
            root_vue.paidy_available = true;
          }
        } else {
          root_vue.paidy_available = true;
        }
      } else {
        root_vue.paidy_available = false;
      }
    }

