    /**
     * ProjectDataの保存.
     * @private
     * @param draft
     */
    function _saveProjectData(draft) {
      // TODO 変更あった場合のみuploadするようにする
      var deferreds = [];
      if (root_vue.template_type != 0) {
        deferreds.push(qrates.modules.Item.uploadCanvasImage($('#label-a-texture').get(0), 'a_label_image_file_id', root_vue));
        deferreds.push(qrates.modules.Item.uploadCanvasImage($('#label-b-texture').get(0), 'b_label_image_file_id', root_vue));
      }
      if (root_vue.vinyl_type == 3) {
        deferreds.push(qrates.modules.Item.uploadCanvasImage($('#vinyl-a-texture').get(0), 'a_vinyl_texture_file_id', root_vue));
        // 現状、両面同じ画像なので、あえて #vinyl-a-texture を送信している
        deferreds.push(qrates.modules.Item.uploadCanvasImage($('#vinyl-a-texture').get(0), 'b_vinyl_texture_file_id', root_vue));
      }

      $('.container > .loading_translucent').addClass('state_loading');

      $.when.apply($, deferreds)
      .fail(function(e) {
        console.log('fail', e);
        window.alert('Some errors occured on upload imaged data');
      })
      .done(function(r) {
        $.ajax({
          type: 'POST',
          url: '/vinylize/save',
          data: qrates.modules.ItemParamBuilder.buildPostData(root_vue, track_list, product_options, draft),
          dataType: 'json',
          success: function (response) {
            if (response.result == 'OK') {
              root_vue.item_id = response.item.id;
              root_vue.vinyl_id = response.item.vinyl.id;
              // vinyl_track / track
              var type_names = ['bonus', 'a_side', 'b_side', 'c_side', 'd_side'];
              var indexes = { bonus: 0, a_side: 0, b_side: 0, c_side: 0, d_side: 0 };
              // TODO: idを一致させるための仕組みが必要(client側でunique採番するなど)
              var new_track_list = { bonus: [], a_side: [], b_side: [], c_side: [], d_side: [] };
              for (var t = 0; t < type_names.length; t++) {
                for (var i = 0; i < track_list[type_names[t]].length; i++) {
                  var vinyl_track = track_list[type_names[t]][i];
                  if (!vinyl_track._destroy) {
                    vinyl_track.index = indexes[type_names[t]]++;
                    new_track_list[type_names[t]].push(vinyl_track);
                  }
                }
              }

              indexes = { bonus: 0, a_side: 0, b_side: 0, c_side: 0, d_side: 0 };
              if (response.item.vinyl.vinyl_tracks) {
                for(var i = 0; i < response.item.vinyl.vinyl_tracks.length; i++) {
                  var vinyl_track = response.item.vinyl.vinyl_tracks[i];
                  var side = type_names[vinyl_track.side];
                  if (side && indexes[side] < new_track_list[side].length) {
                    new_track_list[side][indexes[side]].vinyl_track_id = vinyl_track.id;
                    new_track_list[side][indexes[side]].track_id = vinyl_track.track.id;
                    indexes[side]++;
                  } else {
                    console.log("unexpected responsed track list", side, indexes[side], new_track_list[side].length);
                    window.alert('unexpected responsed track list');
                  }
                }
              }
              track_list = new_track_list;
              root_vue.product_id = response.item.product.id;

              // TODO: idを一致させるための仕組みが必要(client側でunique採番するなど)
              // 暫定対処として、_destroyフラグがついているものを一度消す
              var new_product_options = [];
              for (var i = 0, index = 0; i < product_options.length; i++) {
                var product_option = product_options[i];
                if (!product_option._destroy) {
                  product_option.id = response.item.product.product_options[index].id;
                  product_option.index = index;
                  product_option.component.index = index;
                  new_product_options.push(product_option);
                  index++;
                }
              }
              product_options = new_product_options;

              root_vue.digital_product_id = response.item.digital_product.id;
              root_vue.item_page_design.id = response.item.item_page_design.id;

              form.showError(false);
              trackListVM.showError(false);
              //_pageDesignVM.clearError();

              if (draft) {
                $('.state-error').removeClass('state-error');
                $('.field_with_errors_addon').removeClass('field_with_errors_addon');
                root_vue.dirty = false;
                if (response.errors) {
                  $('.container > .loading_translucent').removeClass('state_loading');
                  window.alert(response.message);
                  var selectorMap = _buildSelectorMap();
                  _applyValidationErrorsRecursively([], response.errors, selectorMap);
                  $('.item_settings_inner').scrollTop(0); // TODO 不要?
                  $('#tracks_upload_content').scrollTop(0); // TODO 不要?
                  return;
                }

                $('.container > .loading_translucent').removeClass('state_loading');
                window.alert(response.message);
                root_vue.publishable = true;
                root_vue.saved_urls = response.urls;
                return;
              }
              root_vue.dirty = false;
              root_vue.no_profile = response.no_profile;
              if (response.errors) {
                $('.state-error').removeClass('state-error');
                $('.field_with_errors_addon').removeClass('field_with_errors_addon');
                _applyValidationErrorsRecursively([], response.errors, _buildSelectorMap());
                $('#modal_confirm').hide();
                return;
              }

              if (root_vue.is_published()) {
                $('.container > .loading_translucent').removeClass('state_loading');
              } else {
                if (Number(response.item.product.self_purchase_quantity) != 0) {
                  location.href = response.urls.project_page + '?show_complete=false&spq=' + response.item.product.self_purchase_quantity;
                } else {
                  location.href = response.urls.project_page + '?show_complete=true';
                }
              }
            } else {
              console.log(response.errors);
              $('#modal_confirm').hide();
              $('.state-error').removeClass('state-error');
              $('.field_with_errors_addon').removeClass('field_with_errors_addon');
              var selectorMap = _buildSelectorMap();
              _applyValidationErrorsRecursively([], response.errors, selectorMap);
              $('.container > .loading_translucent').removeClass('state_loading');
            }

            if(!root_vue.is_published()) {
              $('.container > .loading_translucent').removeClass('state_loading');
            }
          },
          error: function (xhr, status, error) {
            $('.container > .loading_translucent').removeClass('state_loading');
            window.alert(xhr.responseText);
          }
        });
      })
    }

