    function _applyValidationErrorsRecursively(keys, errors, selectorMap) {
      for (var key in errors) {
        if (Array.isArray(errors[key])) {
          _applyValidationErrors(keys.concat(key), errors[key], selectorMap);
        } else {
          _applyValidationErrorsRecursively(keys.concat(key), errors[key], selectorMap)
        }
      }
    }

    function _applyValidationErrors(keys, errors, selectorMap) {
      console.log(keys, errors);
      var map_key = keys.join('.');
      if (!selectorMap.hasOwnProperty(map_key)) {
        console.log(map_key + ' selector not found');
        console.log(selectorMap);
        return;
      }
      var param = selectorMap[map_key];
      $(param.selector).addClass('field_with_errors_addon');

      var error_form = null;
      if (param.category === 'settings') {
        error_form = form;
        $('.vinylize-tab-header-settings').addClass('state-error');
      } else if (param.category === 'tracks') {
        error_form = trackListVM;
        $('.vinylize-tab-header-tracks').addClass('state-error');
      } else if (param.category === 'visuals') {
        $('.vinylize-tab-header-visual').addClass('state-error');
      } else if (param.category === 'page') {
        $('.vinylize-tab-header-display').addClass('state-error');
        //_pageDesignVM.setErrorMessage(errors);
      }

      if (error_form !== null) {
        if (!error_form.visibleError()) {
          error_form.showError(true);
        }
        error_form.setErrorMessage(errors);
      }
    }

