    /**
     * dropzoneの設定.
     * @private
     */
    function _setupDropzone($target) {
      /**
       * Aside, Bside, Front Sleeve, Back Sleeveの画像領域.
       */
      $target.each(function(i, obj){
        var $dom = $(obj);
        var file_type = $dom.data('file-type');
        var type = $dom.data('type');
        var preview = $dom.find('.dropzone_preview');
        var maxFiles = (file_type === 'track') ? null : 1;
        var maxFilesize = (file_type === 'track') ? 500 : 2;
        var token = $("meta[name='csrf-token']").attr('content');
        var $progress = $(obj).find('.dropzone_progress');
        var $progress_inner = ($progress) ? $progress.find('.uploaded_progress__inner') : undefined;

        var dropzone = $dom.dropzone({
          url: '/upload/' + file_type,
          previewsContainer: false,
          addRemoveLinks: true,
          maxFilesize: maxFilesize,
          maxFiles: maxFiles,
          headers: {'X-CSRF-Token':token},
          //autoProcessQueue:false,
          sending: function(file, xhr, fd) {
            console.log('dropzone: sending', file);
          },
          accept: function(_file, done) {
            if ($progress) {
                $progress.show();
            }
            if (type === 'product_option_image') {
              var product_option = product_options[$(this.element).parents('.option-detail').index()];
              _file.target = product_option.component;
            }
            done();
          },
          uploadprogress: function(_file, _progress, _size){
            if (_file.target) {
              _file.target.progress = Math.floor(_progress);
            }
            if ($progress_inner) {
              $progress_inner.width("" + _progress + "%");
            }
            if (_progress == 100) {
              //if (_file.target && (type === 'a_side' || type === 'b_side' || type === 'c_side' || type === 'd_side' || type === 'bonus')) {
              if (_file.target && _is_track(type)) {
                $(_file.target.$el).addClass('status_loaded');
              }
            }
          },
          success: function(_file, _return, _xml){
            console.log('dropzone: success', _file, _return);
            if ($progress) {
              $progress.hide();
              $progress_inner.width("0%");
            }
            if (_return.result === true) {
              if (type === 'a_side_label') {
                  root_vue.a_label_image_file_id = _return.file_id;
                  _cacheImages.side_a_label = _return.url;
                  project.Util.loadImage(_return.url, function(img){
                    console.log('a side label', img);
                    vv && vv.label.sideATexture(img);
                    vv && vv.view(11);
                    display.updateLabelSideATexture(img);
                    _resumeVVRendering();
                  });
              } else if (type === 'b_side_label') {
                root_vue.b_label_image_file_id = _return.file_id;
                _cacheImages.side_b_label = _return.url;
                project.Util.loadImage(_return.url, function(img){
                  console.log('b side label', img);
                  vv && vv.label.sideBTexture(img);
                  vv && vv.view(12);
                  display.updateLabelSideBTexture(img);
                  _resumeVVRendering();
                });
              } else if (type === 'front_sleeve') {
                root_vue.front_sleeve_image_file_id = _return.file_id;
                project.Util.loadImage(_return.url, function(img){
                  vv && vv.sleeve.frontTexture(img);
                  vv && vv.view(13);
                  display.updateSleeveFrontTexture(img);
                  _resumeVVRendering();
                });
              } else if (type === 'back_sleeve') {
                root_vue.back_sleeve_image_file_id = _return.file_id;
                project.Util.loadImage(_return.url, function (img) {
                  vv && vv.sleeve.backTexture(img);
                  vv && vv.view(14);
                  display.updateSleeveBackTexture(img);
                  _resumeVVRendering();
                });
              } else if (_is_track(type)) {
                var type_code = _getVinylSideIdFromString(type);

                _file.target.audio_file_id = _return.file_id;
                _file.target.duration = Math.floor(_return.duration);
                _file.target.url = _return.url;
                _file.target.waveform_url_for_css = 'url(' + _return.waveform_url + ')';
                _file.target.sample_url = _return.sample_url;

                var img = new Image();
                img.src = _return.waveform_url;
                img.onload = function(e) {
                  $(_file.target.$el).addClass('status_generated');
                  $(_file.target.$el).find('.uploaded_track').attr('data-audio-state', 'pause');
                };
                _updateTrackInfo(type_code);
              } else if (type == 'product_option_image') {
                console.log('preview changing', preview, $dom);
                var $container = $dom.parents('.upload_image');
                var $preview = $container.find('.dropzone_preview');
                $preview.find('img').remove();
                $preview.append($("<img />").attr({"src": _return.url, "width": 140, "height": 140}));
                $container.addClass('status_uploaded')

                _file.target.image_file_id = _return.file_id;
              } else if (type === 'page_design_background') {
                display.updateBackgroundImage(_return.url, _return.file_id);
              }
            } else {
              console.log('upload error', _return.message, type);
              alert(_return.message);
              if (_is_track(type)) {
                _removeTrackView(_file.target);
              }
            }
          },
          error: function(_file, _error_msg){
            console.log('dropzone: error', _error_msg, type);
            alert(_error_msg);
            if (_is_track(type)) {
              _removeTrackView(_file.target);
            }
          },
          addedfile: function(file) {
            if (maxFiles && this.files[1]!=null){
              this.removeFile(this.files[0]);
            }
          },
          init: function() {
            // 無理やり
            var dropzone = this;
            $dom.find('.dropzone_btn').on('click', function() {
              $dom.click();
            })
          }
        });
      });
    }
