    var _calculate_options_sales = function() {
      var active_options = _.filter(root_vue.product_options, function(option){
        return (option._destroy === undefined || option._destroy == false );
      });
      if (active_options.length === 0) {
        root_vue.options_total_sales = 0;
        root_vue.options_sum_stock_quantity = 0;
        return;
      }
      root_vue.options_total_sales = _.reduce(
        _.map(active_options, function(option) {
          return Math.floor(option.price * option.total_stock_quantity);
        }), function(memo, num) { return memo + num});

      root_vue.options_sum_stock_quantity = parseInt(_.reduce(
        _.map(active_options, function(option) {
          return option.total_stock_quantity;
        }), function(memo, num) { return memo + parseInt(num)}, 0));
    };

