		function _uploadCanvasImage(canvas, id_param, root_vue) {
			var d = new $.Deferred;
			console.log("_uploadCanvasImage", canvas, id_param);
			var dataUrl = canvas.toDataURL("image/png");
			var bin = atob(dataUrl.replace(/^.*,/, ''));
			var buffer = new Uint8Array(bin.length);
			for (var i = 0; i < bin.length; i++) {
				buffer[i] = bin.charCodeAt(i);
			}
			console.log("captured image size: ", bin.length);
			var blob = new Blob([buffer.buffer], { type: 'image/png' });
			var fd = new FormData();
			fd.append('file', blob);
			$.ajax({
				type: "POST",
				url: "/upload/texture",
				data: fd,
				processData: false,
				contentType: false,
				success: function(response) {
					console.log("_uploadCanvasImage successed", id_param, response);
					root_vue[id_param] = response.file_id;
					d.resolve(response);
				},
				error: function(xhr, status, error) {
					console.log("_uploadCanvasImage errored", id_param, response);
					d.reject(error);
				}
			});
			return d;
		}

