			var $loading = $('.loading_translucent');
			$loading.addClass('state_loading');
			var interval = 2000;
			var limit = 10;
			var count = 0;
			var polling = function() {
				$.ajax({
					type: 'GET',
					url: '/vinylize/' + item_id + '/rendered',
					dataType: 'json',
					success: function(response) {
						$loading.removeClass('state_loading');
						callback(response.rendered_image);
					},
					error: function(xhr, status, error) {
						if (++count < limit) {
							setTimeout(polling, interval);
						} else {
							$loading.removeClass('state_loading');
							callback(null);
						}
					}
				});
			};
			polling();
