(function (global, $) {
  $(document).on('ready page:load', function () {
    var $playingTrack = null;
    var audio = new Audio();
    var soundCloudPlayers = [];
    audio.autoplay = false;

    function changePlayingTrack($targetTrack) {
      var d = $.Deferred();
      if ($playingTrack == null || $playingTrack.get(0) != $targetTrack.get(0)) {
        _.each(soundCloudPlayers, function(object){
          object.player.seek(0);
          object.player.pause();
        });
        $playingTrack = $targetTrack;
        var file = $playingTrack.data('audio');
        if (file) {
          audio.src = file;
          d.resolve(audio);
        } else {
          var trackId = $playingTrack.data('sound-cloud-id');
          var duration = $playingTrack.attr('data-sound-cloud-duration');
          var players = _.filter(soundCloudPlayers, function(player){
            return player.id == trackId;
          });
          if (players.length) {
            d.resolve(players[0].player);
          } else {
            if ($playingTrack.data('require-track-data')) {
              SC.get('/tracks/' + trackId).then(function(track){
                $playingTrack.attr('data-sound-cloud-duration', track.duration);
                $playingTrack.find('.track__wave_image').css("background-image", "url('" + track.waveform_url + "')");
                duration = track.duration;
                SC.stream('/tracks/' + trackId).then(function(player){
                  player.on('finish', function(){
                    $playingTrack.attr('data-audio-state', 'pause');
                  });
                  player.on('time', function(){
                    $playingTrack.find('[data-audio-seekbar]').css('width', (player.currentTime() / duration * 100) + '%');
                  });
                  soundCloudPlayers.push({id:trackId, player:player});
                  d.resolve(player);
                });
              });
            } else {
              SC.stream('/tracks/' + trackId).then(function(player){
                player.on('finish', function(){
                  $playingTrack.attr('data-audio-state', 'pause');
                });
                player.on('time', function(){
                  $playingTrack.find('[data-audio-seekbar]').css('width', (player.currentTime() / duration * 100) + '%');
                });
                soundCloudPlayers.push({id:trackId, player:player});
                d.resolve(player);
              });
            }
          }
        }
        // 一旦全てpauseステートにする
        $('[data-audio], [data-sound-cloud-id]').attr('data-audio-state', 'pause');
        $('[data-audio] [data-audio-seekbar], [data-sound-cloud-id] [data-audio-seekbar]').css('width', 0);
      } else {
        var trackId = $playingTrack.data('sound-cloud-id');
        if (trackId) {
          d.resolve(_.filter(soundCloudPlayers, function(player){return player.id == trackId;})[0].player);
        } else {
          d.resolve(audio);
        }
      }
      return d.promise();
    }

    // 再生ボタンクリック
    $('#container').on('click', '[data-audio] [data-audio-btn], [data-sound-cloud-id] [data-audio-btn]', function (event) {
      var $this = $(this);
      var $targetTrack = $(event.currentTarget).parents('[data-audio]');
      if ($targetTrack.length === 0) {
        $targetTrack = $(event.currentTarget).parents('[data-sound-cloud-id]');
      }
      changePlayingTrack($targetTrack).then(function(_audio){
        var state = $playingTrack.attr('data-audio-state');
        if (state === 'pause') {
          _audio.play();
          $playingTrack.attr('data-audio-state', 'play');
          if ($this.attr('data-activity-type') && $this.attr('data-view-type') != 'preview') {
            var data = {
              'activity': {
                item_id: $this.data('item-id'),
                artist_id: $this.data('artist-id'),
                activity_type: $this.data('activity-type')
              }
            };
            $.ajax({
              type: "POST",
              url: "/activities",
              dataType: 'json',
              contentType: 'application/json',
              data: JSON.stringify(data),
              cache: false,
              success: function (json) {
              },
              error: function (xhr, status, error) {
                console.log('error', xhr.responseText, status, error);
              }
            });
          }
        } else {
          _audio.pause();
          $playingTrack.attr('data-audio-state', 'pause');
        }
      });
    });

    // 波形クリック (seek)
    $('#container').on('click', '[data-audio] [data-audio-waveform], [data-sound-cloud-id] [data-audio-waveform]', function (event) {
      console.log('seek');
      var $targetTrack = $(event.currentTarget).parents('[data-audio]');
      if ($targetTrack.length === 0) {
        $targetTrack = $(event.currentTarget).parents('[data-sound-cloud-id]');
      }
      changePlayingTrack($targetTrack).then(function(_audio){
        var trackId = $targetTrack.data('sound-cloud-id');
        var duration = $targetTrack.data('sound-cloud-duration');
        if (trackId && duration) {
          console.log('seek1', duration * (event.offsetX / $(event.target).width()));
          _audio.seek(duration * (event.offsetX / $(event.target).width()));
          console.log('seek2');
        } else {
          _audio.currentTime = audio.duration * (event.offsetX / $(event.target).width());
        }
        _audio.play();
        $playingTrack.attr('data-audio-state', 'play');
      });
    });

    // update seekbar position
    audio.addEventListener('timeupdate', function () {
      $playingTrack.find('[data-audio-seekbar]').css('width', (audio.currentTime / audio.duration * 100) + '%');
    });

    // finish playing
    audio.addEventListener('ended', function () {
      $playingTrack.attr('data-audio-state', 'pause');
    });

  });
})(this, jQuery);
